<node CREATED="1280298198078" ID="Freemind_Link_930408347" MODIFIED="1280298248609" TEXT="featural models">
<node CREATED="1280298491078" FOLDED="true" ID="Freemind_Link_847841563" MODIFIED="1280298503796" TEXT="subjective assessments of similarity did not always satisfy the assumptions of geometric models of similarity.">
<node CREATED="1280298512750" ID="Freemind_Link_405527287" MODIFIED="1280298547515" TEXT="Three assumptions of standard geometric models of similarity">
<node CREATED="1280298575984" ID="Freemind_Link_1853575570" MODIFIED="1280298578312" TEXT="Minimality: D(A,B) &#x2265; D(A,A) = 0">
